// ========= калькулятор 1

let btn = document.querySelector('.calk')
btn.addEventListener('click', function () {
	let dlina = document.querySelector('#dlina').value
	let shirina = document.querySelector('#shirina').value
	let visota = document.querySelector('#visota').value
	let select = document.querySelector('#select')
	let count = document.querySelector('#count').value
	let days = document.querySelector('#days').value

	let info = document.querySelector('.calculator__info')
	let cifra = document.querySelector('.num')
	let fixPrice = 540


	if (select.value == 'sm') {
		dlina = dlina / 100
		shirina = shirina / 100
		visota = visota / 100
	}

	let tsena = dlina * shirina * visota * count * days * fixPrice
	console.log(tsena)
	cifra.innerHTML = Math.ceil((tsena) * 100) / 100
	document.querySelector('.kub').innerHTML = Math.ceil((dlina * shirina * visota * count) * 10000) / 10000
	info.classList.add('_active')

})


// ------------------

let cec2 = document.querySelector('#cec2')
let cec3 = document.querySelector('#cec3')
let ves2 = document.querySelector('#ves2')
let tov2 = document.querySelector('#tov2')
let opas = document.querySelector('._opas')

cec2.addEventListener('change', function () {
	if (cec2.checked) {
		opas.style.display = 'flex'
	}
})
cec3.addEventListener('change', function () {
	if (cec3.checked) {
		opas.style.display = 'none'
		ves2.value = 0

	}
})


let buttonn = document.querySelector('.buttonn')
buttonn.addEventListener('click', function () {
	let a = tov2.value * 20
	document.querySelector('#one').innerHTML = a

	let b
	if (tov2.value > 1) {
		b = 960 + ((tov2.value - 1) * 120)
	} else {
		b = 960
	}
	document.querySelector('#two').innerHTML = b
	let c
	if (document.querySelector('#sellll').value == 'gr') {
		c = ves2.value / 1000 * tov2.value
	} else {
		c = ves2.value * tov2.value
	}

	console.log(c)
	if (c == 0) {
		d = 0
	}
	if (c <= 3 && c != 0) {
		d = 700
	}
	if (c > 3 && c <= 7) {
		d = 900
	}
	if (c > 7 && c <= 10) {
		d = 1200
	}
	if (c > 10 && c <= 15) {
		d = 1500
	}
	if (c > 15 && c <= 30) {
		d = 1500
	}
	console.log(d)
	document.querySelector('#thre').innerHTML = d
	document.querySelector('#kg').innerHTML = Math.ceil((c) * 100) / 100
	document.querySelector('#itog').innerHTML = a + b + d
})